  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarHeader,
  SidebarMenu,
  SidebarMenuBadge,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarSeparator,
  useSidebar,
} from "@/components/ui/sidebar";
import { cn } from "@/lib/utils";

interface NavItem {
  title: string;
  href: string;
  icon: React.ReactNode;
  badge?: {
    text: string;
  };
}

interface SidebarProps {
  collapsed?: boolean;
  onToggle?: () => void;
  className?: string;
}

export function BrandSidebar({
  collapsed = false,
  onToggle,
  className,
}: SidebarProps) {
  const pathname = usePathname();
  const { state } = useSidebar();
  const isCollapsed = state === "collapsed";

  const mainNavItems: NavItem[] = [
    {
      title: "Dashboard",
      href: "#",
      icon: <Home className="size-4" />,
    },
    {
      title: "Sales",
      href: "#sales",
      icon: <BarChart2 className="size-4" />,
    },
    {
      title: "Expenses",
      href: "#expenses",
      icon: <Database className="size-4" />,
    },
    {
      title: "Banking",
      href: "#banking",
      icon: <Table className="size-4" />,
    },
    {
      title: "Reports",
      href: "#reports",
      icon: <MessageSquareText className="size-4" />,
    },
  ];

  const toolsNavItems: NavItem[] = [
    {
      title: "Taxes",
      href: "#taxes",
      icon: <AlertTriangle className="size-4" />,
    },
    {
      title: "Payroll",
      href: "#payroll",
      icon: <BarChart2 className="size-4" />,
    },
    {
      title: "Accounting",
      href: "#accounting",
      icon: <Clock className="size-4" />,
    },
    {
      title: "Settings",
      href: "#settings",
      icon: <MoreHorizontal className="size-4" />,
    },
  ];

  return (
    <Sidebar
      variant="sidebar"
      collapsible="icon"
      className="mt-16 bg-[var(--color-container-background-secondary)] border-r border-[var(--color-container-border-primary)]"
    >
      <SidebarHeader>
        <div className={cn(isCollapsed ? "py-2" : "p-2")}>
          <Button
            className={cn(
              isCollapsed ? "h-8 w-8 p-0" : "w-full",
              "bg-[var(--color-ui-primary)] text-[var(--color-text-accent)] hover:bg-[var(--color-ui-primary-hover)]"
            )}
          >
            <Plus className={cn("size-4", !isCollapsed && "mr-1")} />
            {!isCollapsed && <span>Create</span>}
          </Button>
        </div>
      </SidebarHeader>

      <SidebarContent>
        {/* Main Nav Items */}
        <SidebarGroup>
          <SidebarGroupContent>
            <SidebarMenu>
              {mainNavItems.map((item) => (
                <SidebarMenuItem key={item.href}>
                  <SidebarMenuButton
                    asChild
                    isActive={
                      pathname === item.href ||
                      (pathname === "" && item.href === "/")
                    }
                    tooltip={item.title}
                    className="text-[var(--color-text-primary)] hover:bg-[var(--color-container-background-accent-hover)] data-[active=true]:bg-[var(--color-container-background-accent-selected)] data-[active=true]:text-[var(--color-text-primary)]"
                  >
                    <Link href={item.href}>
                      {item.icon}
                      <span>{item.title}</span>
                    </Link>
                  </SidebarMenuButton>
                  {item.badge && (
                    <SidebarMenuBadge
                      className={cn(
                        "border border-[var(--color-container-border-primary)] bg-[var(--color-container-background-accent)] text-[var(--color-text-primary)]"
                      )}
                    >
                      {item.badge.text}
                    </SidebarMenuBadge>
                  )}
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>

        <SidebarSeparator className="border-[var(--color-container-border-tertiary)]" />

        {/* Tools Nav Items */}
        <SidebarGroup>
          <SidebarGroupContent>
            <SidebarMenu>
              {toolsNavItems.map((item) => (
                <SidebarMenuItem key={item.href}>
                  <SidebarMenuButton
                    asChild
                    isActive={pathname === item.href}
                    tooltip={item.title}
                    className="text-[var(--color-text-primary)] hover:bg-[var(--color-container-background-accent-hover)] data-[active=true]:bg-[var(--color-container-background-accent-selected)] data-[active=true]:text-[var(--color-text-primary)]"
                  >
                    <Link href={item.href}>
                      {item.icon}
                      <span>{item.title}</span>
                    </Link>
                  </SidebarMenuButton>
                  {item.badge && (
                    <SidebarMenuBadge
                      className={cn(
                        "border border-[var(--color-container-border-primary)] bg-[var(--color-container-background-accent)] text-[var(--color-text-primary)]"
                      )}
                    >
                      {item.badge.text}
                    </SidebarMenuBadge>
                  )}
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>
    </Sidebar>
  );
}
