files.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 222 11% 23%;

    --card: 0 0% 100%;
    --card-foreground: 222 11% 23%;

    --popover: 0 0% 100%;
    --popover-foreground: 222 11% 23%;

    --primary: 112 70% 37%;
    --primary-foreground: 0 0% 100%;

    --secondary: 223 4% 23%;
    --secondary-foreground: 0 0% 100%;

    --muted: 220 13% 93%;
    --muted-foreground: 223 3% 44%;

    --accent: 220 20% 96%;
    --accent-foreground: 222 11% 23%;

    --destructive: 4 76% 47%;
    --destructive-foreground: 0 0% 100%;

    --border: 220 3% 57%;
    --input: 220 3% 57%;
    --ring: 209 100% 39%;

    --radius: 0.5rem;
  }

  [data-theme="quickbooks"][data-colorscheme="dark"] {
    --background: 0 0% 16%;
    --foreground: 220 13% 93%;

    --card: 240 3% 22%;
    --card-foreground: 220 13% 93%;

    --popover: 240 3% 22%;
    --popover-foreground: 220 13% 93%;

    --primary: 96 100% 35%;
    --primary-foreground: 0 0% 16%;

    --secondary: 223 4% 23%;
    --secondary-foreground: 0 0% 100%;

    --muted: 223 4% 23%;
    --muted-foreground: 220 4% 76%;

    --accent: 223 4% 23%;
    --accent-foreground: 220 13% 93%;

    --destructive: 6 100% 74%;
    --destructive-foreground: 0 0% 16%;

    --border: 220 4% 76%;
    --input: 220 3% 57%;
    --ring: 196 100% 60%;
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground font-sans;
  }
}

/** @type {import('tailwindcss').Config} */
module.exports = {
  content: [
    "./src/pages/**/*.{js,ts,jsx,tsx,mdx}",
    "./src/components/**/*.{js,ts,jsx,tsx,mdx}",
    "./src/app/**/*.{js,ts,jsx,tsx,mdx}",
  ],
  darkMode: "class",
  theme: {
    extend: {
      colors: {
        // Base colors
        background: "var(--background)",
        foreground: "var(--foreground)",
        card: "var(--card)",
        "card-foreground": "var(--card-foreground)",
        popover: "var(--popover)",
        "popover-foreground": "var(--popover-foreground)",

        // Primary colors
        primary: {
          DEFAULT: "var(--primary)",
          foreground: "var(--primary-foreground)",
        },
        secondary: {
          DEFAULT: "var(--secondary)",
          foreground: "var(--secondary-foreground)",
        },

        // UI elements
        muted: {
          DEFAULT: "var(--muted)",
          foreground: "var(--muted-foreground)",
        },
        accent: {
          DEFAULT: "var(--accent)",
          foreground: "var(--accent-foreground)",
        },

        // State colors
        destructive: {
          DEFAULT: "var(--destructive)",
          foreground: "var(--destructive-foreground)",
        },

        // Border and input
        border: "var(--border)",
        input: "var(--input)",
        ring: "var(--ring)",

        // Chart colors
        chart: {
          1: "var(--chart-1)",
          2: "var(--chart-2)",
          3: "var(--chart-3)",
          4: "var(--chart-4)",
          5: "var(--chart-5)",
        },

        // Sidebar
        sidebar: {
          DEFAULT: "var(--sidebar)",
          foreground: "var(--sidebar-foreground)",
          primary: "var(--sidebar-primary)",
          "primary-foreground": "var(--sidebar-primary-foreground)",
          accent: "var(--sidebar-accent)",
          "accent-foreground": "var(--sidebar-accent-foreground)",
          border: "var(--sidebar-border)",
          ring: "var(--sidebar-ring)",
        },
      },
      fontFamily: {
        sans: "var(--font-sans)",
        serif: "var(--font-serif)",
        mono: "var(--font-mono)",
      },
      borderRadius: {
        DEFAULT: "var(--radius)",
      },
      boxShadow: {
        "2xs": "var(--shadow-2xs)",
        xs: "var(--shadow-xs)",
        sm: "var(--shadow-sm)",
        DEFAULT: "var(--shadow)",
        md: "var(--shadow-md)",
        lg: "var(--shadow-lg)",
        xl: "var(--shadow-xl)",
        "2xl": "var(--shadow-2xl)",
      },
    },
  },
  plugins: [],
};

import { VercelToolbar } from "@vercel/toolbar/next";
import { Geist } from "next/font/google";
import React, { type ReactNode } from "react";

import { cn } from "@/lib/utils";

import "@/app/globals.css";

const geist = Geist({ subsets: ["latin"] });

export default function RootLayout({
  children,
}: Readonly<{
  children: ReactNode;
}>) {
  return (
    <html lang="en" className={cn(geist.className, "bg-background")}>
      <body>
        <main className="mt-16 flex w-full justify-center">
          <div className="container">{children}</div>
        </main>

        {process.env.NODE_ENV === "development" && <VercelToolbar />}
      </body>
    </html>
  );
}
