@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Base colors */
  --background: #f5f5f5;
  --foreground: #2c2c2c;
  --card: #ffffff;
  --card-foreground: #2c2c2c;
  --popover: #ffffff;
  --popover-foreground: #2c2c2c;

  /* Primary colors - QuickBooks blue */
  --primary: #2CA01C;
  --primary-foreground: #ffffff;
  --secondary: #f0f0f0;
  --secondary-foreground: #2c2c2c;

  /* UI elements */
  --muted: #f5f5f5;
  --muted-foreground: #666666;
  --accent: #2CA01C;
  --accent-foreground: #ffffff;

  /* State colors */
  --destructive: #dc2626;
  --destructive-foreground: #ffffff;

  /* Border and input */
  --border: #e5e5e5;
  --input: #e5e5e5;
  --ring: #2CA01C;

  /* Chart colors */
  --chart-1: #2CA01C;
  --chart-2: #4CAF50;
  --chart-3: #81C784;
  --chart-4: #A5D6A7;
  --chart-5: #C8E6C9;

  /* Sidebar */
  --sidebar: #2c2c2c;
  --sidebar-foreground: #ffffff;
  --sidebar-primary: #2CA01C;
  --sidebar-primary-foreground: #ffffff;
  --sidebar-accent: #2CA01C;
  --sidebar-accent-foreground: #ffffff;
  --sidebar-border: #404040;
  --sidebar-ring: #2CA01C;

  /* Typography */
  --font-sans: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  --font-serif: Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;

  /* Border radius */
  --radius: 0.375rem;

  /* Shadows */
  --shadow-2xs: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  --shadow-xs: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  --shadow-sm: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
  --shadow: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);
}

/* Dark mode variables */
[data-theme="dark"] {
  --background: #282828;
  --foreground: #eceef1;
  --card: #363639;
  --card-foreground: #eceef1;
  --popover: #363639;
  --popover-foreground: #eceef1;

  --primary: #53b700;
  --primary-foreground: #282828;
  --secondary: #393a3d;
  --secondary-foreground: #eceef1;

  --muted: #393a3d;
  --muted-foreground: #babec5;
  --accent: #53b700;
  --accent-foreground: #282828;

  --destructive: #ff8979;
  --destructive-foreground: #282828;

  --border: #6b6c72;
  --input: #6b6c72;
  --ring: #53b700;

  --chart-1: #34bfff;
  --chart-2: #008481;
  --chart-3: #9457fa;
  --chart-4: #ff9331;
  --chart-5: #f25a52;

  --sidebar: #282828;
  --sidebar-foreground: #eceef1;
  --sidebar-primary: #53b700;
  --sidebar-primary-foreground: #282828;
  --sidebar-accent: #53b700;
  --sidebar-accent-foreground: #282828;
  --sidebar-border: #6b6c72;
  --sidebar-ring: #53b700;
}

@layer base {
  body {
    @apply bg-background text-foreground;
  }
}

/** @type {import('tailwindcss').Config} */
module.exports = {
  darkMode: "class",
  theme: {
    extend: {
      colors: {
        background: "var(--background)",
        foreground: "var(--foreground)",
        primary: {
          DEFAULT: "var(--primary)",
          foreground: "var(--primary-foreground)",
        },
        secondary: {
          DEFAULT: "var(--secondary)",
          foreground: "var(--secondary-foreground)",
        },
        accent: {
          DEFAULT: "var(--accent)",
          foreground: "var(--accent-foreground)",
        },
        muted: {
          DEFAULT: "var(--muted)",
          foreground: "var(--muted-foreground)",
        },
        card: {
          DEFAULT: "var(--card)",
          foreground: "var(--card-foreground)",
        },
        popover: {
          DEFAULT: "var(--popover)",
          foreground: "var(--popover-foreground)",
        },
        border: "var(--border)",
        input: "var(--input)",
        ring: "var(--ring)",
        destructive: {
          DEFAULT: "var(--destructive)",
          foreground: "var(--destructive-foreground)",
        },
        chart: {
          1: "var(--chart-1)",
          2: "var(--chart-2)",
          3: "var(--chart-3)",
          4: "var(--chart-4)",
          5: "var(--chart-5)",
        },
        sidebar: {
          DEFAULT: "var(--sidebar)",
          foreground: "var(--sidebar-foreground)",
          primary: {
            DEFAULT: "var(--sidebar-primary)",
            foreground: "var(--sidebar-primary-foreground)",
          },
          accent: {
            DEFAULT: "var(--sidebar-accent)",
            foreground: "var(--sidebar-accent-foreground)",
          },
          border: "var(--sidebar-border)",
          ring: "var(--sidebar-ring)",
        },
      },
      borderRadius: {
        sm: "calc(var(--radius) - 4px)",
        md: "calc(var(--radius) - 2px)",
        lg: "var(--radius)",
        xl: "calc(var(--radius) + 4px)",
      },
      boxShadow: {
        "2xs": "var(--shadow-2xs)",
        xs: "var(--shadow-xs)",
        sm: "var(--shadow-sm)",
        DEFAULT: "var(--shadow)",
        md: "var(--shadow-md)",
        lg: "var(--shadow-lg)",
        xl: "var(--shadow-xl)",
        "2xl": "var(--shadow-2xl)",
      },
    },
  },
  plugins: [],
};

import { VercelToolbar } from "@vercel/toolbar/next";
import { Geist } from "next/font/google";
import React, { type ReactNode } from "react";

import { cn } from "@/lib/utils";

import "@/app/globals.css";

const geist = Geist({ subsets: ["latin"] });

export default function RootLayout({
  children,
}: Readonly<{
  children: ReactNode;
}>) {
  return (
    <html lang="en" className={cn(geist.className, "bg-background")}>
      <body>
        <main className="mt-16 flex w-full justify-center">
          <div className="container">{children}</div>
        </main>

        {process.env.NODE_ENV === "development" && <VercelToolbar />}
      </body>
    </html>
  );
}
